import { Component, OnInit} from '@angular/core';
import { FaceSnap } from '../models/face-snap.models';
import { FaceSnapsServices } from '../services/face-snap.service';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-single-face-snap',
  templateUrl: './single-face-snap.component.html',
  styleUrls: ['./single-face-snap.component.scss']
})
export class SingleFaceSnapComponent implements OnInit{
  faceSnap! : FaceSnap | undefined
  buttonText!: string;

  constructor(
    private faceSnapsService : FaceSnapsServices,
    private route : ActivatedRoute
    ){}

  ngOnInit() {
    this.buttonText= 'Oh Snap!'
    const faceSnapId : string | null = this.route.snapshot.paramMap.get('id')
    if(faceSnapId){
    this.faceSnapsService.getFaceSnapById(+faceSnapId)
    .subscribe(faceSnap => this.faceSnap = faceSnap)
  }}
