import { Component, Input, OnInit } from '@angular/core';
import { FaceSnap } from '../models/face-snap.models';

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent implements OnInit {
  @Input() faceSnap! : FaceSnap

  buttonText!: string;

  ngOnInit() {

    this.buttonText= 'Oh Snap!'
  }
  onSnap() {
    if( this.buttonText === "Oh Snap!")
    {
      this.faceSnap.snaps++;
      this.buttonText ="Oh noo!"
    }else {
      this.faceSnap.snaps--;
      this.buttonText ="Oh Snap!"
    }
  }
}
<div class="face-snap-card"
[ngClass]="{snapped : buttonText === 'Oh Snap!'}"
>
<h2>{{ faceSnap.title | titlecase }}</h2>
<img [src]="faceSnap.imageUrl" [alt]="faceSnap.title" >
<p>{{ faceSnap.description }}</p>
<p *ngIf="faceSnap.location"> photo prise à {{ faceSnap.location }}</p>
<p>Mise en ligne le {{ faceSnap.createdDate | date: 'longDate'}}</p>
<p>
  <button (click)="onSnap()">{{buttonText}}</button>
 <span [ngStyle]="{ color: 'rgb(0,'+ faceSnap.snaps +',0)'}"> {{ faceSnap.snaps }}</span>
</p>
</div>
