import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent {
  title! : string;
  description! : string;
  createDate! : Date;
  snaps! : number;
  imageUrl! : string;

  ngOnInit() {
    this.title = "blopblop"
    this.description = "C'est toi"
    this.createDate = new Date()
    this.snaps = 6
    this.imageUrl = 'https://cdn.pixabay.com/photo/2015/05/31/16/03/teddy-bear-792273_1280.jpg'

  }
  onAddSnap() {
    this.snaps;
  }
  onSnap() {
    this.snaps;
  }
}
<div class="face-snap-card">
<h2>{{ title }}</h2>
<img [src]="imageUrl" [alt]="title" >
<p>{{ description }}</p>
<p>Mise en ligne le {{ createDate }}</p>
<p>
  if(<button (click)="onAddSnap()">Oh snap!</button>
  {{ snaps }})
  else if (<button (click)="onAddSnap()">Oh snoop!</button>
  {{ snaps }})

</p>
</div>
