import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  constructor (private router : Router) {

  }
  ngOnInit(): void {

  }

  onAddNewFaceSnap(): void {
    const currentUrl = this.router.url
    if(currentUrl === "/facesnaps"){
      this.router.navigateByUrl("facesnaps/create")
    } else if (currentUrl === "/culinaire") {
      this.router.navigateByUrl("culinaire/create")
    }
  }

  isFaceSnapRoute(): void {
    {}
  }
}
<header>
  <h1>ExpSharing</h1>
  <nav>
    <a routerLink="" routerLinkActive="active" [routerLinkActiveOptions]="{exact : true}">Home</a>
    <a routerLink="facesnaps" routerLinkActive="active">Exp Destination</a>
    <a routerLink="culinaire" routerLinkActive="active">Exp Culinaire</a>
    <a routerLink="" routerLinkActive="active">Exp Jeux Vidéo</a>

  </nav>
  <button *ngIf="isFaceSnapRoute()" (click)="onAddNewFaceSnap()">+</button>
</header>
