  providedIn:'root'
})
export class GameServices {
  faceSnap! : FaceSnap
  constructor(private http: HttpClient){}

  getAllGame(): Observable<FaceSnap[]> {
    return this.http.get<FaceSnap[]>('http://localhost:3000/game')
